import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [videoOn, setVideoOn] = useState(false);
  const [stats, setStats] = useState({ count: 0, history: [] });

  useEffect(() => {
    const interval = setInterval(() => {
      fetch('/api/get_stats')
        .then(response => response.json())
        .then(data => setStats(data));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const toggleVideo = () => {
    const action = videoOn ? 'stop_video' : 'start_video';
    fetch(`/api/${action}`)
      .then(response => response.json())
      .then(() => setVideoOn(!videoOn));
  };

  return (
    <Container fluid>
      <Row className="mt-4">
        <Col md={8}>
          <Card>
            <Card.Body>
              <Card.Title>Video Feed</Card.Title>
              <img 
                src="/api/video_feed" 
                alt="Video feed" 
                style={{ display: videoOn ? 'block' : 'none', width: '100%' }}
              />
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Statistics</Card.Title>
              <p>Count: {stats.count}</p>
              <h6>Card History:</h6>
              <div className="card-history">
                {stats.history.map((card, index) => (
                  <div key={index}>{`${card[0]} of ${card[1]}`}</div>
                ))}
              </div>
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Controls</Card.Title>
              <Button onClick={toggleVideo}>
                {videoOn ? 'Stop Video' : 'Start Video'}
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
